mapper.update:
UPDATE REQUESTS_T
 SET REQUEST_STATUS = #{requestStatus},
  PIC_NO = #{picNo}
 WHERE REQUEST_NO = #{requestNo}

UPDATE REQUESTS_T
   SET REQUEST_STATUS = #{requestStatus},
       PIC_NO = #{picNo},
       REJECT_REASON = #{rejectReason}
 WHERE REQUEST_NO = #{requestNo}

UPDATE REQUESTS_T
   SET REASON = #{reason}
   WHERE REQUEST_NO = #{requestNo}

UPDATE REQUEST_ATTACH_T
   SET UPLOAD_PATH =  #{uploadPath},
       FILESYSTEM_NAME = #{filesystemName},
       ORIGINAL_FILENAME = #{originalFileName}
 WHERE REQUEST_NO = #{requestNo}
