mapper.update:

UPDATE BBS_T
   SET GROUP_ORDER = GROUP_ORDER + 1
 WHERE GROUP_NO = #{groupNo}
   AND GROUP_ORDER > #{groupOrder}

mapper.insert:

INSERT INTO BBS_T (
    BBS_NO
  , CONTENTS
  , USER_NO
  , CREATE_DT
  , STATE
  , DEPTH
  , GROUP_NO
  , GROUP_ORDER
) VALUES (
    BBS_SEQ.NEXTVAL
  , #{contents}
  , #{user.userNo}
  , CURRENT_DATE
  , 1
  , #{depth}
  , #{groupNo}
  , #{groupOrder}
)

