mapper.insert:

  SELECT UPLOAD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO UPLOAD_T (
    UPLOAD_NO
  , TITLE
  , CONTENTS
  , CREATE_DT
  , MODIFY_DT
  , USER_NO
) VALUES (
    #{uploadNo}
  , #{title}
  , #{contents}
  , TO_CHAR(CURRENT_DATE, 'YYYY-MM-DD HH24:MI:SS')
  , TO_CHAR(CURRENT_DATE, 'YYYY-MM-DD HH24:MI:SS')
  , #{user.userNo}
)

INSERT INTO ATTACH_T (
    ATTACH_NO
  , UPLOAD_PATH
  , FILESYSTEM_NAME
  , ORIGINAL_FILENAME
  , DOWNLOAD_COUNT
  , HAS_THUMBNAIL
  , UPLOAD_NO
) VALUES (
    ATTACH_SEQ.NEXTVAL
  , #{uploadPath}
  , #{filesystemName}
  , #{originalFilename}
  , 0
  , #{hasThumbnail}
  , #{uploadNo}
)

mapper.select:
SELECT ATTACH_NO, UPLOAD_PATH, FILESYSTEM_NAME, ORIGINAL_FILENAME, DOWNLOAD_COUNT, HAS_THUMBNAIL
  FROM ATTACH_T
 WHERE UPLOAD_NO = #{uploadNo}

SELECT ATTACH_NO, UPLOAD_PATH, FILESYSTEM_NAME, ORIGINAL_FILENAME, DOWNLOAD_COUNT, HAS_THUMBNAIL
  FROM ATTACH_T
 WHERE ATTACH_NO = #{attachNo}

mapper.update:
UPDATE ATTACH_T
   SET DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1
 WHERE ATTACH_NO = #{attachNo}

UPDATE UPLOAD_T
   SET TITLE = #{title}
     , CONTENTS = #{contents}
     , MODIFY_DT = TO_CHAR(CURRENT_DATE, 'YYYY-MM-DD HH24:MI:SS')
 WHERE UPLOAD_NO = #{uploadNo}
