mapper.select:
SELECT EMPLOYEE_NO, NAME, EMAIL, PHONE, ADDRESS, DEPARTMENT_NO, RANK_NO, PASSWORD, PROFILE_PICTURE_PATH, EMPLOYEE_STATUS, HIRE_DATE, EXIT_DATE
  FROM EMPLOYEES_T

      EMAIL = #{email}
      AND PASSWORD = #{password}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , EMAIL
  , PW
  , NAME
  , GENDER
  , MOBILE
  , EVENT_AGREE
  , SIGNUP_KIND
  , PW_MODIFY_DT
  , SIGNUP_DT
) VALUES (
    USER_SEQ.NEXTVAL
  , #{email}
  , #{pw}
  , #{name}
  , #{gender}
  , #{mobile}
  , #{eventAgree}
  , 0
  , CURRENT_DATE
  , CURRENT_DATE
)

INSERT INTO ACCESS_HISTORY_T (
    ACCESS_HISTORY_NO
  , EMAIL
  , IP
  , USER_AGENT
  , SESSION_ID
  , SIGNIN_DT
  , SIGNOUT_DT
) VALUES (
    ACCESS_HISTORY_SEQ.NEXTVAL
  , #{email}
  , #{ip}
  , #{userAgent}
  , #{sessionId}
  , CURRENT_DATE
  , NULL
)

mapper.update:
UPDATE ACCESS_HISTORY_T
   SET SIGNOUT_DT = CURRENT_DATE
 WHERE SESSION_ID = #{sessionId}
